(1981, '9287149224', 'La Politique de la jeunesse au Luxembourg', 'rapport', NULL, '2015-10-16', 58, 'http://books.google.fr/books/content?id=TvZYt4fI5x8C&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'http://books.google.fr/books?id=TvZYt4fI5x8C&dq=politique&as_pt=BOOKS&hl=&source=gbs_api', 509, '35.70', 2),
(1982, '9789264263512', 'STI Revue, Volume 1998 Numéro 1 Politique de l\'innovation et de la technologie : nouveaux fondements et nouvelles approches', 'Politique de l\'innovation et de la technologie : nouveaux fondements et nouvelles approches', 'Le changement technologique a un potentiel considérable : on pourra grâce à lui améliorer la productivité de l\'économie entière et dynamiser croissance et création d\'emplois. Mais, pour le réaliser pleinement, les pouvoirs publics doivent faire des ...', '1998-09-14', 372, 'http://books.google.fr/books/content?id=IJPYAgAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'http://books.google.fr/books?id=IJPYAgAAQBAJ&dq=politique&as_pt=BOOKS&hl=&source=gbs_api', 178, '46.00', 2),
